<script lang="ts" setup>
import { onMounted } from 'vue';
import monitorStatus from '@/views/home/_monitor-status/monitor-status.vue';
import { menuStateResult, monitorScreenResult, gamingResult } from '@/service/monitor-state-result';
import { toDisplayTimeFormat } from '@/service/service';
import iconSvg from '@/views/home/_menus/_components/_icon-svg.vue';
import CrosshairConfigureNodes from '@/models/class/gaming/_crosshair/_configure-nodes';


const CrosshairConfigureNodesEnum = new CrosshairConfigureNodes();

const props = defineProps({
    openMonitor: {
        type: Boolean,
        default: false
    },
    screenInitial: {
        type: Boolean,
        default: false
    },
    showMonitorStatus: {
        type: Boolean,
        default: false
    },
    showScreen: {
        type: Boolean,
        default: false
    },
    startUpFinish: {
        type: Boolean,
        default: false
    },
    showGamingSettingText: {
        type: Boolean,
        default: false  
    },
    showGamingCrosshair: {
        type: Boolean,
        default: false  
    }
});

const emit = defineEmits(['update:screenInitial', 'update:showMonitorStatus', 'update:showScreen', 'update:startUpFinish']);

function handleScreen() {
    setTimeout(() => {
        emit('update:showScreen', true);
        emit('update:showMonitorStatus', true);
        handleMonitorStatus();
    }, 2000);
};

function handleMonitorStatus() {
    setTimeout(() => {
        emit('update:showMonitorStatus', false);
        emit('update:startUpFinish', true);
    }, 5000);
};

onMounted(() => {
    if(props.openMonitor) {
        if(menuStateResult.value.OSDMessage.powerOnLogo) {
            emit('update:screenInitial', true);

            setTimeout(() => {
                emit('update:screenInitial', false);

                handleScreen();
            }, 2000);
        } else {
            handleScreen();
        }
    }
});

</script>
<style lang="scss" scoped>
@use "@/styles/_var.scss" as *;
@use "@/styles/mixin/gaming-setting.scss" as *;

.screen-initial {
