.screen {
    --rgb-r: v-bind("monitorScreenResult.RGB.R");
    --rgb-g: v-bind("monitorScreenResult.RGB.G");
    --rgb-b: v-bind("monitorScreenResult.RGB.B");
    position: absolute;
    width: $screen-width;
    height: $screen-height;
    top: 1px;
    left: 0px;
