    &.FilltoAspectRatio {
        max-width: 682px;
        height: $screen-height;
        margin: 0 50px;

        &::before {
            width: 682px;
            height: $screen-height;
        }
    }

    &::before {
        position: absolute;
        content: "";
        top: 0;
        left: 0;
        right: 0;
        bottom: 0;
        width: $screen-width;
        height: $screen-height;
        background-color: rgb(var(--rgb-r), var(--rgb-g), var(--rgb-b));
        z-index: 1;
        mix-blend-mode: multiply;
    }

    img {
        width: $screen-width;
        height: $screen-height;
        position: absolute;
        bottom: v-bind("monitorScreenResult.imagePosition.y");
        left:  v-bind("monitorScreenResult.imagePosition.x");
        filter: 
            // hue-rotate(v-bind("monitorScreenResult.RGB"))
            brightness(v-bind("monitorScreenResult.brightness"))
            contrast(v-bind("monitorScreenResult.contrast"))
            blur(v-bind("monitorScreenResult.sharpness"));
    }

    &.diagnostic-patterns {
        background-color: v-bind("monitorScreenResult.diagnosticPatterns.result");
    }

    &.multi-monitor-align {
        z-index: 3;
        background-color: $black-16;
        svg {
            width: $screen-width;
